pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IERC1155MintBurn.sol";
import "./interfaces/IAMM.sol";

contract AMM is IAMM, Initializable {
    using SafeERC20 for IERC20;

    // todo: Change this interface
    IERC1155MintBurn public element;
    address public studio;
    uint256 public totalFeeNumerator;
    uint256 public artistFeeNumerator;
    uint256 constant FEE_DENOMINATOR = 1_000_000_000;
    uint256 public platformRevenue;

    // tokenID => BondingCurve
    mapping(uint256 => BondingCurve) public tokenIdToBondingCurve;
    mapping(address => uint256) public artistRevenues;

    modifier onlyStudio() {
        require(
            msg.sender == studio,
            "Only the Studio contract can call this function"
        );
        _;
    }

    function initialize(
        address _element,
        address _studio,
        uint256 _totalFeeNumerator,
        uint256 _artistFeeNumerator
    ) external initializer {
        element = IERC1155MintBurn(_element);
        studio = _studio;
        totalFeeNumerator = _totalFeeNumerator;
        artistFeeNumerator = _artistFeeNumerator;
    }

    function createBondingCurves(
        uint256[] calldata _tokenIds,
        uint256[] calldata _constantAs,
        uint256[] calldata _constantBs,
        address _artistAddress,
        address _erc20Token,
        uint256 _startTime
    ) external onlyStudio {
        require(
            _tokenIds.length == _constantAs.length &&
                _tokenIds.length == _constantBs.length,
            "Invalid array lengths"
        );
        for (uint256 i; i < _tokenIds.length; i++) {
            createBondingCurve(
                _tokenIds[i],
                _constantAs[i],
                _constantBs[i],
                _artistAddress,
                _erc20Token,
                _startTime
            );
        }
    }

    function createBondingCurve(
        uint256 _tokenId,
        uint256 _constantA,
        uint256 _constantB,
        address _artistAddress,
        address _erc20Token,
        uint256 _startTime
    ) public onlyStudio {
        require(
            _artistAddress != address(0),
            "Artist address cannot be address zero"
        );
        require(
            tokenIdToBondingCurve[_tokenId].artistAddress == address(0),
            "Bonding curve already initialized"
        );

        tokenIdToBondingCurve[_tokenId] = BondingCurve(
            _constantA,
            _constantB,
            0,
            _artistAddress,
            _erc20Token,
            _startTime
        );

        emit BondingCurveCreated(
            _tokenId,
            _constantA,
            _constantB,
            _artistAddress,
            _erc20Token,
            _startTime
        );
    }

    function buyElements(
        uint256 _tokenId,
        uint256 _erc1155Quantity,
        uint256 _maxERC20ToSpend,
        address _spender,
        address _recipient
    ) public onlyStudio {
        (
            uint256 erc20TotalAmount,
            uint256 erc20TotalFee,
            uint256 erc20ArtistFee
        ) = getBuyERC20AmountWithFee(_tokenId, _erc1155Quantity);

        require(erc20TotalAmount <= _maxERC20ToSpend, "Slippage too high");

        IERC20(tokenIdToBondingCurve[_tokenId].erc20Token).safeTransferFrom(
            _spender,
            address(this),
            erc20TotalAmount
        );
        // platformRevenue += erc20TotalFee - erc20ArtistFee;
        // artistRevenues[
        //     tokenIdToBondingCurve[_tokenId].artistAddress
        // ] += erc20ArtistFee;
        tokenIdToBondingCurve[_tokenId].reserves += (erc20TotalAmount -
            erc20TotalFee);

        element.mint(_recipient, _tokenId, _erc1155Quantity);

        // emit ElementsBought(
        //     _bondingCurveCreator,
        //     _tokenId,
        //     _erc1155Quantity,
        //     erc20TotalAmount,
        //     erc20TotalFee,
        //     erc20ArtistFee,
        //     _recipient
        // );
    }

    function batchBuyElements(
        uint256[] memory _tokenIds,
        uint256[] memory _erc1155Quantities,
        uint256[] memory _maxERC20sToSpend,
        address _spender,
        address _recipient
    ) external onlyStudio {
        require(
            _tokenIds.length == _erc1155Quantities.length &&
                _tokenIds.length == _maxERC20sToSpend.length,
            "Invalid array lengths"
        );

        for (uint256 i; i < _tokenIds.length; i++) {
            buyElements(
                _tokenIds[i],
                _erc1155Quantities[i],
                _maxERC20sToSpend[i],
                _spender,
                _recipient
            );
        }
    }

    function sellElements(
        uint256 _tokenId,
        uint256 _erc1155Quantity,
        uint256 _minERC20ToReceive,
        address _erc20Recipient
    ) public onlyStudio {
        require(
            block.timestamp >= tokenIdToBondingCurve[_tokenId].startTime,
            "AMM has not started yet"
        );
        uint256 erc20TotalAmount = getSellERC20Amount(
            _tokenId,
            _erc1155Quantity
        );
        require(erc20TotalAmount >= _minERC20ToReceive, "Slippage too high");

        tokenIdToBondingCurve[_tokenId].reserves -= erc20TotalAmount;

        element.burn(msg.sender, _tokenId, _erc1155Quantity);

        IERC20(tokenIdToBondingCurve[_tokenId].erc20Token).safeTransfer(
            _erc20Recipient,
            erc20TotalAmount
        );

        // emit ElementsSold(
        //     _bondingCurveCreator,
        //     _tokenId,
        //     _erc1155Quantity,
        //     erc20TotalAmount,
        //     _recipient
        // );
    }

    function batchSellElements(
        uint256[] memory _tokenIds,
        uint256[] memory _erc1155Quantities,
        uint256[] memory _minERC20sToReceive,
        address _erc20Recipient
    ) external onlyStudio {
        require(
            _tokenIds.length == _erc1155Quantities.length &&
                _tokenIds.length == _minERC20sToReceive.length,
            "Invalid array lengths"
        );

        for (uint256 i; i < _tokenIds.length; i++) {
            sellElements(
                _tokenIds[i],
                _erc1155Quantities[i],
                _minERC20sToReceive[i],
                _erc20Recipient
            );
        }
    }

    // function claimPlatformRevenue(address _recipient) external onlyOwner {
    //     uint256 _platformRevenue = platformRevenue;
    //     platformRevenue = 0;

    //     weth.transfer(_recipient, _platformRevenue);

    //     emit PlatformRevenueClaimed(_recipient, _platformRevenue);
    // }

    // function claimArtistRevenue(address _recipient) external {
    //     require(
    //         artistRevenues[msg.sender] > 0,
    //         "You do not have an available balance"
    //     );

    //     uint256 claimedRevenue = artistRevenues[msg.sender];
    //     artistRevenues[msg.sender] = 0;

    //     weth.safeTransfer(_recipient, claimedRevenue);

    //     emit ArtistRevenueClaimed(_recipient, claimedRevenue);
    // }

    function getBuyERC20AmountWithFee(
        uint256 _tokenId,
        uint256 _erc1155Quantity
    )
        public
        view
        returns (
            uint256 erc20TotalAmount,
            uint256 erc20TotalFee,
            uint256 erc20ArtistFee
        )
    {
        uint256 nominalERC20Amount = getBuyERC20Amount(
            _tokenId,
            _erc1155Quantity
        );
        erc20TotalFee =
            (nominalERC20Amount * totalFeeNumerator) /
            FEE_DENOMINATOR;
        erc20ArtistFee =
            (nominalERC20Amount * artistFeeNumerator) /
            FEE_DENOMINATOR;
        erc20TotalAmount = nominalERC20Amount + erc20TotalFee;
    }

    function getBuyERC20Amount(uint256 _tokenId, uint256 _erc1155Quantity)
        public
        view
        returns (uint256 erc20Amount)
    {
        require(
            block.timestamp >= tokenIdToBondingCurve[_tokenId].startTime,
            "AMM has not started yet"
        );
        require(
            tokenIdToBondingCurve[_tokenId].artistAddress != address(0),
            "Bonding curve not initialized"
        );

        // reserves = (a * supply) + (b * supply)^2
        uint256 newElementSupply = element.totalSupply(_tokenId) +
            _erc1155Quantity;

        erc20Amount =
            ((tokenIdToBondingCurve[_tokenId].constantA * newElementSupply) +
                (tokenIdToBondingCurve[_tokenId].constantB *
                    newElementSupply) **
                    2) -
            tokenIdToBondingCurve[_tokenId].reserves;
    }

    function getSellERC20Amount(uint256 _tokenId, uint256 _erc1155Quantity)
        public
        view
        returns (uint256 erc20Amount)
    {
        require(
            block.timestamp >= tokenIdToBondingCurve[_tokenId].startTime,
            "AMM has not started yet"
        );
        require(
            tokenIdToBondingCurve[_tokenId].artistAddress != address(0),
            "Bonding curve not initialized"
        );
        require(
            element.totalSupply(_tokenId) >= _erc1155Quantity,
            "Quantity greater than total supply"
        );
        // reserves = (a * supply) + (b * supply)^2
        uint256 newElementSupply = element.totalSupply(_tokenId) -
            _erc1155Quantity;

        erc20Amount =
            tokenIdToBondingCurve[_tokenId].reserves -
            ((tokenIdToBondingCurve[_tokenId].constantA * newElementSupply) +
                (tokenIdToBondingCurve[_tokenId].constantB *
                    newElementSupply) **
                    2);
    }
}
","keccak256":"0xefc38b2cac72dca6641f0da89f4e9ff9b52f5237e3acca0d3562d05c85a30191","license":"Unlicense"},"contracts/Canvas.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./interfaces/ICanvas.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";

contract Canvas is ICanvas, Initializable, ERC721BurnableUpgradeable {
    uint256 nextProjectId = 1;
    mapping(uint256 => ProjectData) public projects;

    function initialize() external initializer {
        __ERC721_init("Intrinsic.art Canvases", "INSC");
        __ERC721Burnable_init();
    }

    function createProject(
        address _studio,
        address _minter,
        uint256 _maxInvocations
    ) public returns (uint256 projectId) {
        require(
            _maxInvocations < 1_000_000,
            "Max invocations must be less than 1,000,000"
        );

        projectId = nextProjectId;

        projects[projectId].studio = _studio;
        projects[projectId].minter = _minter;
        projects[projectId].invocations = 0;
        projects[projectId].maxInvocations = _maxInvocations;

        nextProjectId++;
    }

    function mint(uint256 _projectId, address _to)
        public
        returns (uint256 tokenId)
    {
        require(
            msg.sender == projects[_projectId].minter,
            "Only minter can call this function"
        );
        require(
            (projects[_projectId].invocations) <=
                projects[_projectId].maxInvocations,
            "This project has sold out"
        );
        tokenId = (_projectId * 1_000_000) + projects[_projectId].invocations;
        projects[_projectId].invocations++;

        _safeMint(_to, tokenId);

        emit MintedToken(_to, _projectId, tokenId);
    }

    function getProjectIdFromCanvasId(uint256 canvasId)
        public
        pure
        returns (uint256 projectId)
    {
        projectId = canvasId / 1_000_000;
    }

    /////// View Functions ///////////
    // function tokenURI(uint256 _tokenId)
    //     public
    //     view
    //     override
    //     returns (string memory)
    // {
    //     return
    // }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable)
        returns (bool)
    {
        return
            interfaceId == type(ERC721BurnableUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }
}
","keccak256":"0x288648f3fc1c12d950818643444929ad5237deb23c0bab7be06622ab5a5b7ba6","license":"Unlicense"},"contracts/DutchAuction.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/IDutchAuction.sol";
import "./interfaces/IERC721Mint.sol";
import "./Canvas.sol";

contract DutchAuction is IDutchAuction, Initializable {
    using SafeERC20 for IERC20;

    mapping(uint256 => Auction) public projectIdToAuction;
    Canvas public canvas;
    address public studio;
    mapping(address => mapping(address => uint256))
        public artistToERC20Balances;

    modifier onlyStudio() {
        require(
            msg.sender == studio,
            "Only the Studio contract can call this function"
        );
        _;
    }

    function initialize(address _canvas, address _studio) external initializer {
        canvas = Canvas(_canvas);
        studio = _studio;
    }

    function addAuction(uint256 _projectId, Auction memory _auction)
        external
        onlyStudio
    {
        require(
            projectIdToAuction[_projectId].erc20Token == address(0),
            "Dutch Auction already initialized"
        );
        require(
            _auction.startPrice >= _auction.endPrice,
            "Start price must be greater than or equal end price"
        );
        require(
            _auction.endTime >= _auction.startTime,
            "End time must be after start time"
        );

        projectIdToAuction[_projectId] = Auction(
            _auction.startTime,
            _auction.endTime,
            _auction.startPrice,
            _auction.endPrice,
            _auction.artistAddress,
            _auction.erc20Token
        );

        emit AuctionAdded(
            msg.sender,
            _projectId,
            _auction.startTime,
            _auction.endTime,
            _auction.startPrice,
            _auction.endPrice,
            _auction.artistAddress
        );
    }

    // todo: this should return array of canvas IDs that were bought
    function buyCanvases(
        uint256 _projectId,
        uint256 _quantity,
        address _spender,
        address _recipient
    ) external onlyStudio returns (uint256[] memory canvasIds) {
        canvasIds = new uint256[](_quantity);

        require(
            block.timestamp >= projectIdToAuction[_projectId].startTime,
            "Auction has not started yet"
        );
        uint256 canvasesTotalPrice = getCanvasPrice(_projectId) * _quantity;

        // Safe transfer to the revenue claimer?
        IERC20(projectIdToAuction[_projectId].erc20Token).safeTransferFrom(
            _spender,
            address(this),
            canvasesTotalPrice
        );

        for (uint256 i; i < _quantity; i++) {
            canvasIds[i] = canvas.mint(_projectId, _recipient);
        }

        artistToERC20Balances[projectIdToAuction[_projectId].artistAddress][
            projectIdToAuction[_projectId].erc20Token
        ] += canvasesTotalPrice;

        emit CanvasesBought(_projectId, _quantity, canvasesTotalPrice);
    }

    function getCanvasPrice(uint256 _projectId)
        public
        view
        returns (uint256 canvasPrice)
    {
        if (block.timestamp < projectIdToAuction[_projectId].startTime) {
            // Auction hasn't started yet
            canvasPrice = projectIdToAuction[_projectId].startPrice;
        } else if (block.timestamp > projectIdToAuction[_projectId].endTime) {
            // Auction has ended
            canvasPrice = projectIdToAuction[_projectId].endPrice;
        } else {
            // Auction is active
            canvasPrice =
                projectIdToAuction[_projectId].startPrice -
                (
                    (((block.timestamp -
                        projectIdToAuction[_projectId].startTime) *
                        (projectIdToAuction[_projectId].startPrice -
                            projectIdToAuction[_projectId].endPrice)) /
                        (projectIdToAuction[_projectId].endTime -
                            projectIdToAuction[_projectId].startTime))
                );
        }
    }
}
","keccak256":"0xa6ed9809d45504a5b61e770a1ae35f24c122c5c1d5db9d942b53bc9c75805235","license":"Unlicense"},"contracts/Element.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./interfaces/IElement.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";

contract Element is IElement, ERC1155, ERC1155Burnable, ERC1155Supply {
    uint256 public nextTokenId = 1;
    mapping(uint256 => Feature) public features;

    constructor() ERC1155("") {}

    function createFeature(string calldata _label, address _minter)
        public
        returns (uint256 tokenId)
    {
        tokenId = nextTokenId;
        nextTokenId++;

        features[tokenId].label = _label;
        features[tokenId].minter = _minter;
    }

    function createFeatures(
        string[] calldata _labels,
        address _minter
    ) public returns (uint256[] memory tokenIds) {
        tokenIds = new uint256[](_labels.length);

        for (uint256 i; i < _labels.length; i++) {
            tokenIds[i] = createFeature(_labels[i], _minter);
        }
    }

    function mint(
        address _to,
        uint256 _id,
        uint256 _amount
    ) public {
        require(msg.sender == features[_id].minter, "Only minter can mint");

        _mint(_to, _id, _amount, bytes(""));
    }

    function mintBatch(
        address _to,
        uint256[] memory _ids,
        uint256[] memory _amounts
    ) public {
        require(_ids.length == _amounts.length, "Invalid array lengths");
        for (uint256 i; i < _ids.length; i++) {
            mint(_to, _ids[i], _amounts[i]);
        }
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function getElementLabel(uint256 _tokenId) public view returns (string memory) {
      return features[_tokenId].label;
    }
}
","keccak256":"0x19cca0b9c88cede76fd74eb62814925cc9c6757f6302a83a32365674cea82974","license":"Unlicense"},"contracts/Studio.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./interfaces/IStudio.sol";
import "./Canvas.sol";
import "./DutchAuction.sol";
import "./Element.sol";
import "./AMM.sol";

contract Studio is IStudio, Initializable, ERC1155Holder {
    // todo: convert these to interfaces
    Canvas public canvas;
    DutchAuction public dutchAuction;
    Element public element;
    AMM public amm;

    mapping(uint256 => ProjectData) public projects;
    mapping(uint256 => CanvasData) public canvases;
    mapping(address => uint256) public userNonces;

    modifier onlyArtist(uint256 projectId) {
        require(
            msg.sender == projects[projectId].artistAddress,
            "You are not the project's artist"
        );
        _;
    }

    /////////// Project Functions /////////////
    function initialize(
        address _canvas,
        address _dutchAuction,
        address _element,
        address _amm
    ) external initializer {
        canvas = Canvas(_canvas);
        dutchAuction = DutchAuction(_dutchAuction);
        element = Element(_element);
        amm = AMM(_amm);
    }

    function createProject(
        CreateProjectData memory _createProjectData,
        IDutchAuction.Auction memory _createAuction,
        CreateAMM memory _createAMM
    ) public returns (uint256 projectId, uint256[][] memory elementTokenIds) {
        projectId = canvas.createProject(
            address(this),
            address(dutchAuction),
            _createProjectData.maxInvocations
        );

        _updateProjectData(projectId, _createProjectData);

        dutchAuction.addAuction(projectId, _createAuction);

        elementTokenIds = _createElementsAndAMMs(
            projectId,
            _createProjectData.artistAddress,
            _createProjectData.featureLabels,
            _createAMM
        );

        emit ProjectCreated(projectId);
    }

    function updateScript(uint256 _projectId, uint256 _scriptIndex, string calldata _script) public {
        require(
            msg.sender == projects[_projectId].artistAddress,
            "Only the artist can call this function"
        );

        projects[_projectId].scripts[_scriptIndex] = (_script);
    }

    function buyElements(
        uint256[] memory _elementTokenIds,
        uint256[] memory _elementAmounts,
        uint256[] memory _maxERC20sToSpend
    ) public {
        amm.batchBuyElements(
            _elementTokenIds,
            _elementAmounts,
            _maxERC20sToSpend,
            msg.sender,
            msg.sender
        );
    }

    function buyCanvases(uint256 _projectId, uint256 _quantity)
        public
        returns (uint256[] memory canvasIds)
    {
        canvasIds = dutchAuction.buyCanvases(
            _projectId,
            _quantity,
            msg.sender,
            msg.sender
        );
    }

    function wrap(uint256 _canvasId, uint256[] memory _elementIndexes) public {
        require(
            msg.sender == canvas.ownerOf(_canvasId),
            "You are not the owner of this canvas"
        );
        require(!canvases[_canvasId].wrapped, "Canvas is already wrapped");
        require(
            _elementIndexes.length ==
                projects[getProjectIdFromCanvasId(_canvasId)]
                    .featureCategoryLabels
                    .length,
            "Incorrect elements array length"
        );

        bytes32 newHash = keccak256(
            abi.encodePacked(msg.sender, userNonces[msg.sender])
        );

        canvases[_canvasId].hash = newHash;

        uint256[] memory elementTokenIds = new uint256[](
            _elementIndexes.length
        );

        for (uint256 i; i < _elementIndexes.length; i++) {
            elementTokenIds[i] = projects[getProjectIdFromCanvasId(_canvasId)]
                .featureTokenIds[i][_elementIndexes[i]];

            element.safeTransferFrom(
                msg.sender,
                address(this),
                elementTokenIds[i],
                1,
                ""
            );
        }

        canvases[_canvasId].wrapped = true;
        canvases[_canvasId].wrappedFeatureTokenIds = elementTokenIds;
        userNonces[msg.sender]++;

        emit CanvasWrapped(_canvasId, msg.sender, elementTokenIds);
    }

    function unwrap(uint256 _canvasId) public {
        require(
            msg.sender == canvas.ownerOf(_canvasId),
            "You are not the owner of this canvas"
        );
        require(canvases[_canvasId].wrapped, "Canvas is not wrapped");

        canvases[_canvasId].hash = 0;
        canvases[_canvasId].wrapped = false;

        uint256 projectId = getProjectIdFromCanvasId(_canvasId);

        for (
            uint256 i;
            i < projects[projectId].featureCategoryLabels.length;
            i++
        ) {
            uint256 elementTokenId = canvases[_canvasId].wrappedFeatureTokenIds[
                i
            ];
            canvases[_canvasId].wrappedFeatureTokenIds[i] = 0;
            element.safeTransferFrom(
                address(this),
                msg.sender,
                elementTokenId,
                1,
                ""
            );
        }

        emit CanvasUnwrapped(_canvasId, msg.sender);
    }

    function buyElementsAndWrap(
        uint256[] memory _elementTokenIdsToBuy,
        uint256[] memory _elementAmountsToBuy,
        uint256[] memory _maxERC20ToSpend,
        uint256[] memory _elementIndexesToWrap,
        uint256 _canvasId
    ) public {
        buyElements(
            _elementTokenIdsToBuy,
            _elementAmountsToBuy,
            _maxERC20ToSpend
        );
        wrap(_canvasId, _elementIndexesToWrap);
    }

    function buyCanvasAndElementsAndWrap(
        uint256 _projectId,
        uint256[] memory _elementTokenIdsToBuy,
        uint256[] memory _elementAmountsToBuy,
        uint256[] memory _maxERC20ToSpend,
        uint256[] memory _elementIndexesToWrap
    ) public {
        uint256[] memory canvasIds = buyCanvases(_projectId, 1);
        buyElements(
            _elementTokenIdsToBuy,
            _elementAmountsToBuy,
            _maxERC20ToSpend
        );
        wrap(canvasIds[0], _elementIndexesToWrap);
    }

    function _updateProjectData(
        uint256 projectId,
        CreateProjectData memory _createProjectData
    ) private {
        require(
            _createProjectData.featureCategoryLabels.length ==
                _createProjectData.featureLabels.length,
            "Invalid feature and feature category array lengths"
        );

        projects[projectId].name = _createProjectData.name;
        projects[projectId].description = _createProjectData.description;
        projects[projectId].artistAddress = _createProjectData.artistAddress;
        projects[projectId].artistName = _createProjectData.artistName;
        projects[projectId].website = _createProjectData.website;
        projects[projectId].license = _createProjectData.license;
        projects[projectId].baseURI = _createProjectData.baseURI;
        projects[projectId].scriptJSON = _createProjectData.scriptJSON;
        projects[projectId].scriptCount = _createProjectData.scriptCount;
        projects[projectId].name = _createProjectData.name;
        projects[projectId].featureCategoryLabels = _createProjectData
            .featureCategoryLabels;
    }

    function _createElementsAndAMMs(
        uint256 _projectId,
        address _artistAddress,
        string[][] memory _features,
        CreateAMM memory _createAMM
    ) private returns (uint256[][] memory elementTokenIds) {
        elementTokenIds = new uint256[][](_features.length);
        // Loop through categories
        for (uint256 i; i < _features.length; i++) {
            elementTokenIds[i] = element.createFeatures(
                _features[i],
                address(amm)
            );
            amm.createBondingCurves(
                elementTokenIds[i],
                _createAMM.constantA[i],
                _createAMM.constantB[i],
                _artistAddress,
                _createAMM.erc20Token,
                _createAMM.startTime
            );
        }

        projects[_projectId].featureTokenIds = elementTokenIds;
    }

    /////// View Functions ///////////

    function getCanvasWrappedTokenIds(uint256 _canvasId)
        public
        view
        returns (uint256[] memory)
    {
        return canvases[_canvasId].wrappedFeatureTokenIds;
    }

    function getCanvasWrappedFeatureLabels(uint256 _canvasId)
        public
        view
        returns (string[] memory featureLabels)
    {
        featureLabels = new string[](
            canvases[_canvasId].wrappedFeatureTokenIds.length
        );

        for (uint256 i; i < featureLabels.length; i++) {
            featureLabels[i] = element.getElementLabel(
                canvases[_canvasId].wrappedFeatureTokenIds[i]
            );
        }
    }

    function getUserNonce(address _user) public view returns (uint256) {
        return userNonces[_user];
    }

    // @notice Function for returning a project's feature prices
    function getProjectFeaturePrices(uint256 _projectId)
        public
        view
        returns (uint256[][] memory featurePrices)
    {
        uint256 featureCategoryLength = projects[_projectId]
            .featureCategoryLabels
            .length;
        featurePrices = new uint256[][](featureCategoryLength);

        for (uint256 i; i < featureCategoryLength; i++) {
            uint256 featuresLength = projects[_projectId]
                .featureTokenIds[i]
                .length;
            uint256[] memory innerFeaturePrices = new uint256[](featuresLength);
            for (uint256 j; j < featuresLength; j++) {
                uint256 featureTokenId = projects[_projectId].featureTokenIds[
                    i
                ][j];
                (uint256 featurePrice, , ) = amm.getBuyERC20AmountWithFee(
                    featureTokenId,
                    1
                );
                innerFeaturePrices[j] = featurePrice;
            }
            featurePrices[i] = innerFeaturePrices;
        }
    }

    function getCanvasTokenURI(uint256 _canvasTokenId)
        public
        view
        returns (string memory)
    {
        return
            string(
                abi.encodePacked(
                    projects[getProjectIdFromCanvasId(_canvasTokenId)].baseURI,
                    Strings.toString(_canvasTokenId)
                )
            );
    }

    function getCanvasHash(uint256 _canvasId) public view returns (bytes32) {
        return canvases[_canvasId].hash;
    }

    function getProjectScripts(uint256 _projectId)
        public
        view
        returns (string[] memory _scripts)
    {
        _scripts = new string[](projects[_projectId].scriptCount);

        for(uint256 i; i < projects[_projectId].scriptCount; i++) {
          _scripts[i] = projects[_projectId].scripts[i];
        }
    }

    function getProjectFeatureCategoryLabels(uint256 _projectId)
        public
        view
        returns (string[] memory)
    {
        return projects[_projectId].featureCategoryLabels;
    }

    function getProjectFeatureTokenIds(uint256 _projectId)
        public
        view
        returns (uint256[][] memory)
    {
        return projects[_projectId].featureTokenIds;
    }

    function getProjectFeatureLabels(uint256 _projectId)
        public
        view
        returns (string[][] memory featureLabels)
    {
        uint256 featureCategoryLength = projects[_projectId]
            .featureCategoryLabels
            .length;
        featureLabels = new string[][](featureCategoryLength);

        for (uint256 i; i < featureCategoryLength; i++) {
            uint256 featuresLength = projects[_projectId]
                .featureTokenIds[i]
                .length;
            string[] memory innerFeatureLabels = new string[](featuresLength);
            for (uint256 j; j < featuresLength; j++) {
                innerFeatureLabels[j] = element.getElementLabel(
                    projects[_projectId].featureTokenIds[i][j]
                );
            }
            featureLabels[i] = innerFeatureLabels;
        }
    }

    function getProjectIdFromCanvasId(uint256 canvasId)
        public
        pure
        returns (uint256 projectId)
    {
        projectId = canvasId / 1_000_000;
    }
}
