// SPDX-License-Identifier: GNU GPLv3
pragma solidity =0.8.19;

import {ITraits} from "./interfaces/ITraits.sol";
import {IArtwork} from "./interfaces/IArtwork.sol";
import {IStringStorage} from "./interfaces/IStringStorage.sol";
import {IProjectRegistry} from "./interfaces/IProjectRegistry.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import {ERC2981} from "@openzeppelin/contracts/token/common/ERC2981.sol";
import {PaymentSplitter} from "./PaymentSplitter.sol";
import {IERC721Metadata} from "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import {ERC1155Holder, ERC1155Receiver, IERC165} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

/**
 * Implements ERC-721 standard for artwork tokens, and
 * functionality for minting artwork and reclaiming traits
 */
contract Artwork is IArtwork, IERC721Metadata, ERC2981, ERC721, ERC1155Holder, PaymentSplitter {
    using Strings for uint256;
    using Strings for address;

    bool public proofMinted;
    string public constant VERSION = "1.0";
    address public artistAddress;
    IProjectRegistry public projectRegistry;
    ITraits public traits;
    uint256 public nextTokenId;
    StringStorageData public metadataJSONStringStorage;
    StringStorageData public scriptStringStorage;

    mapping(uint256 => ArtworkData) private artworkData;
    mapping(address => uint256) private userNonces;

    constructor(
        string memory _name,
        string memory _symbol,
        address _artistAddress,
        address _projectRegistry,
        uint96 _royaltyFeeNumerator,
        address[] memory _royaltyPayees,
        uint256[] memory _royaltyShares,
        StringStorageData memory _metadataJSONStringStorage,
        StringStorageData memory _scriptStringStorage
    ) ERC721(_name, _symbol) PaymentSplitter(_royaltyPayees, _royaltyShares) {
        artistAddress = _artistAddress;
        projectRegistry = IProjectRegistry(_projectRegistry);

        // Set EIP-2981 royalties to be sent to this contract
        _setDefaultRoyalty(address(this), _royaltyFeeNumerator);

        metadataJSONStringStorage = _metadataJSONStringStorage;
        scriptStringStorage = _scriptStringStorage;
    }

    /** @inheritdoc IArtwork*/
    function setup(bytes calldata _data) external {
        if (msg.sender != address(projectRegistry))
            revert OnlyProjectRegistry();
        if (address(traits) != address(0)) revert AlreadySetup();

        address _traits = abi.decode(_data, (address));

        traits = ITraits(_traits);
    }

    /** @inheritdoc IArtwork*/
    function mintArtwork(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) public returns (uint256 _artworkTokenId) {
        bytes32 _hash = keccak256(
            abi.encodePacked(
                address(this),
                msg.sender,
                userNonces[msg.sender],
                _saltNonce
            )
        );
        _artworkTokenId = nextTokenId++;
        artworkData[_artworkTokenId].hash = _hash;
        artworkData[_artworkTokenId].traitTokenIds = _traitTokenIds;
        userNonces[msg.sender]++;

        traits.transferTraitsToMintArtwork(msg.sender, _traitTokenIds);
        _safeMint(msg.sender, _artworkTokenId);

        emit ArtworkMinted(_artworkTokenId, _traitTokenIds, _hash, msg.sender);
    }

    /** @inheritdoc IArtwork*/
    function reclaimTraits(uint256 _artworkTokenId) external {
        if (msg.sender != _ownerOf(_artworkTokenId)) revert OnlyArtworkOwner();

        // Clear Artwork state
        uint256[] memory traitTokenIds = artworkData[_artworkTokenId]
            .traitTokenIds;
        artworkData[_artworkTokenId].hash = 0;
        artworkData[_artworkTokenId].traitTokenIds = new uint256[](0);

        uint256[] memory amounts = new uint256[](traitTokenIds.length);
        for (uint256 i; i < amounts.length; ) {
            amounts[i] = 1;
            unchecked {
                ++i;
            }
        }

        _burn(_artworkTokenId);
        traits.safeBatchTransferFrom(
            address(this),
            msg.sender,
            traitTokenIds,
            amounts,
            ""
        );

        emit TraitsReclaimed(_artworkTokenId, msg.sender);
    }

    /** @inheritdoc IArtwork*/
    function mintArtworkProof(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) external {
        if (proofMinted) revert ProofAlreadyMinted();
        if (
            msg.sender != artistAddress &&
            msg.sender != address(projectRegistry)
        ) revert OnlyArtistOrProjectRegistry();

        proofMinted = true;

        traits.mintTraitsArtistProof(msg.sender, _traitTokenIds);

        mintArtwork(_traitTokenIds, _saltNonce);
    }

    /** @inheritdoc IArtwork*/
    function mintArtworkWhitelist(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) external {
        traits.mintTraitsWhitelist(msg.sender, _traitTokenIds);

        mintArtwork(_traitTokenIds, _saltNonce);
    }

    /** @inheritdoc IArtwork*/
    function mintTraitsAndArtwork(
        uint256[] calldata _traitTokenIdsToBuy,
        uint256[] calldata _traitAmountsToBuy,
        uint256[] calldata _traitTokenIdsToCreateArtwork,
        uint256 _saltNonce
    ) external payable {
        traits.mintTraits{value: msg.value}(
            msg.sender,
            _traitTokenIdsToBuy,
            _traitAmountsToBuy
        );

        mintArtwork(_traitTokenIdsToCreateArtwork, _saltNonce);
    }

    /** @inheritdoc IArtwork*/
    function tokenURI(
        uint256 _tokenId
    )
        public
        view
        override(ERC721, IERC721Metadata, IArtwork)
        returns (string memory)
    {
        string memory baseURI = projectRegistry.baseURI();

        return
            bytes(baseURI).length != 0
                ? string(
                    abi.encodePacked(
                        baseURI,
                        address(this).toHexString(),
                        "/",
                        _tokenId.toString()
                    )
                )
                : "";
    }

    /** @inheritdoc IArtwork*/
    function artwork(
        uint256 _artworkTokenId
    )
        external
        view
        returns (
            uint256[] memory _traitTokenIds,
            string[] memory _traitNames,
            string[] memory _traitValues,
            string[] memory _traitTypeNames,
            string[] memory _traitTypeValues,
            bytes32 _hash
        )
    {
        _requireMinted(_artworkTokenId);

        uint256 traitCount = artworkData[_artworkTokenId].traitTokenIds.length;
        _traitTokenIds = artworkData[_artworkTokenId].traitTokenIds;
        _traitNames = new string[](traitCount);
        _traitValues = new string[](traitCount);
        _traitTypeNames = new string[](traitCount);
        _traitTypeValues = new string[](traitCount);

        for (uint256 i; i < traitCount; ) {
            (
                _traitNames[i],
                _traitValues[i],
                _traitTypeNames[i],
                _traitTypeValues[i]
            ) = traits.trait(_traitTokenIds[i]);

            unchecked {
                ++i;
            }
        }

        _hash = artworkData[_artworkTokenId].hash;
    }

    /** @inheritdoc IArtwork*/
    function metadataJSON() external view returns (string memory) {
        return
            IStringStorage(metadataJSONStringStorage.stringStorageAddress)
                .stringAtSlot(metadataJSONStringStorage.stringStorageSlot);
    }

    /** @inheritdoc IArtwork*/
    function script() external view returns (string memory) {
        return
            IStringStorage(scriptStringStorage.stringStorageAddress)
                .stringAtSlot(scriptStringStorage.stringStorageSlot);
    }

    /** @inheritdoc IArtwork*/
    function userNonce(address _user) external view returns (uint256) {
        return userNonces[_user];
    }

    /** @inheritdoc IArtwork*/
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(IERC165, ERC721, ERC1155Receiver, IArtwork, ERC2981)
        returns (bool)
    {
        return
            interfaceId == type(IArtwork).interfaceId ||
            super.supportsInterface(interfaceId);
    }
}

contracts/interfaces/ITraits.sol.content:
// SPDX-License-Identifier: GNU GPLv3
pragma solidity =0.8.19;

import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

interface ITraits is IERC1155 {
    struct TraitType {
        string name;
        string value;
    }

    struct Trait {
        string name;
        string value;
        uint256 typeIndex;
        uint256 maxSupply;
    }

    struct TraitsSetup {
        string[] traitTypeNames;
        string[] traitTypeValues;
        string[] traitNames;
        string[] traitValues;
        uint256[] traitTypeIndexes;
        uint256[] traitMaxSupplys;
    }

    event AuctionUpdated(
        uint256 auctionStartTime,
        uint256 auctionEndTime,
        uint256 auctionStartPrice,
        uint256 auctionEndPrice,
        uint256 auctionPriceSteps,
        bool auctionExponential,
        uint256 traitsSaleStartTime
    );

    event WhitelistUpdated(
        uint256 whitelistStartTime,
        address[] whitelistAddresses,
        uint256[] whitelistAmounts
    );

    event WhitelistArtworkMint(address indexed caller);

    event ProofArtworkMint(address indexed caller);

    error OnlyArtwork();
    error OnlyProjectRegistry();
    error AlreadySetup();
    error AuctionIsLive();
    error InvalidAuction();
    error MaxSupply();
    error InvalidEthAmount();
    error InvalidTraits();
    error AuctionNotLive();
    error InvalidTokenId();
    error TraitsSaleStartTime();
    error WhitelistStartTime();
    error NoWhitelistMints();

    /**
     * Sets the address of the Artwork contract and the auction configuration
     *
     * @param _data bytes data containt the artwork contract address and auction data
     */
    function setup(bytes calldata _data) external;

    /**
     * Updates the schedule of the dutch auction, can only
     * be called if the dutch auction hasn't started yet, and
     * can only update the auction to a later time, not sooner
     *
     * @param _auctionStartTime timestamp the auction begins at
     * @param _auctionEndTime timestamp the auction ends at
     * @param _auctionStartPrice trait price the auction begins at
     * @param _auctionEndPrice trait price the auction ends at
     * @param _auctionPriceSteps number of different prices auction steps through
     * @param _auctionExponential true indicates auction curve is exponential, otherwise linear
     * @param _traitsSaleStartTime timestamp at which traits can be bought individually
     */
    function updateAuction(
        uint256 _auctionStartTime,
        uint256 _auctionEndTime,
        uint256 _auctionStartPrice,
        uint256 _auctionEndPrice,
        uint256 _auctionPriceSteps,
        bool _auctionExponential,
        uint256 _traitsSaleStartTime
    ) external;

    /**
     * Updates the whitelisted addresses and amounts they can claim
     *
     * @param _whitelistStartTime timestamp at which whitelisted users can start minting
     * @param _whitelistAddresses addresses to be whitelisted
     * @param _whitelistAmounts amount of whitelist mints for each address
     */
    function updateWhitelist(
        uint256 _whitelistStartTime,
        address[] memory _whitelistAddresses,
        uint256[] memory _whitelistAmounts
    ) external;

    /**
     * Allows a user to mint any number of traits and amounts using ether
     *
     * @param _recipient the address to receive the trait tokens
     * @param _traitTokenIds the trait token IDs to buy
     * @param _traitAmounts the amounts of each token ID to buy
     */
    function mintTraits(
        address _recipient,
        uint256[] calldata _traitTokenIds,
        uint256[] calldata _traitAmounts
    ) external payable;

    /**
     * Allows the artist to mint traits for free for the proof artwork
     *
     * @param _caller the artist address to receive the trait tokens
     * @param _traitTokenIds the trait token IDs to mint the artwork with
     */
    function mintTraitsArtistProof(
        address _caller,
        uint256[] calldata _traitTokenIds
    ) external;

    /**
     * Allows a whitelisted user to mint traits for free
     *
     * @param _recipient the address to receive the trait tokens
     * @param _traitTokenIds the trait token IDs to mint the artwork with
     */
    function mintTraitsWhitelist(
        address _recipient,
        uint256[] calldata _traitTokenIds
    ) external;

    /**
     * Called by the Artwork contract to transfer traits from the caller to the Artwork
     * contract to create a new Artwork token
     *
     * @param _caller the address creating the artwork
     * @param _traitTokenIds the trait token IDs used to create the artwork
     */
    function transferTraitsToMintArtwork(
        address _caller,
        uint256[] calldata _traitTokenIds
    ) external;

    /**
     * Returns the trait types
     *
     * @return _traitTypeNames human readable trait type names
     * @return _traitTypeValues trait type values used in the generative script
     */
    function traitTypes()
        external
        view
        returns (
            string[] memory _traitTypeNames,
            string[] memory _traitTypeValues
        );

    /**
     * Returns info about an individual trait
     *
     * @param _tokenId token ID to get info about
     * @return _traitName human readable trait name
     * @return _traitValue trait type value used in the generative script
     * @return _traitTypeName human readable trait type name
     * @return _traitTypeValue trait type value used in the generative script
     */
    function trait(
        uint256 _tokenId
    )
        external
        view
        returns (
            string memory _traitName,
            string memory _traitValue,
            string memory _traitTypeName,
            string memory _traitTypeValue
        );

    /**
     * Returns info about all traits
     *
     * @return _traitTokenIds token IDs of the traits
     * @return _traitNames human readable trait names
     * @return _traitValues trait type values used in the generative script
     * @return _traitTypeIndexes trait type index each trait belongs to
     * @return _traitTypeNames human readable trait type names
     * @return _traitTypeValues trait type values used in the generative script
     * @return _traitTotalSupplys total supply of each trait
     * @return _traitMaxSupplys max supply of each trait
     */
    function traits()
        external
        view
        returns (
            uint256[] memory _traitTokenIds,
            string[] memory _traitNames,
            string[] memory _traitValues,
            uint256[] memory _traitTypeIndexes,
            string[] memory _traitTypeNames,
            string[] memory _traitTypeValues,
            uint256[] memory _traitTotalSupplys,
            uint256[] memory _traitMaxSupplys
        );

    /**
     * Returns which price step the auction is currently on
     *
     * @return the current price step
     */
    function traitPriceStep() external view returns (uint256);

    /**
     * Returns the current trait price
     *
     * @return _price the current trait price in ether
     */
    function traitPrice() external view returns (uint256 _price);

    /**
     * Returns how many more whitelist mints the specified address has
     *
     * @return uint256 the number of whitelist mints remaining
     */
    function whitelistMintsRemaining(
        address _user
    ) external view returns (uint256);

    /**
     * Returns the max supply of the specified token ID
     *
     * @return _maxSupply the max supply of the token
     */
    function maxSupply(
        uint256 _tokenId
    ) external view returns (uint256 _maxSupply);

    /**
     * Returns the URI of the specified token ID
     *
     * @param _tokenId the token ID to get the URI for
     * @return string the token specific URI
     */
    function uri(uint256 _tokenId) external view returns (string memory);

    /**
     * Returns whether the specified interface ID is supported by the contract
     *
     * @param interfaceId the interface ID to check
     * @return bool True if the interface is supported, otherwise False
     */
    function supportsInterface(bytes4 interfaceId) external view returns (bool);
}

contracts/interfaces/IArtwork.sol.content:
// SPDX-License-Identifier: GNU GPLv3
pragma solidity =0.8.19;

import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC2981} from "@openzeppelin/contracts/interfaces/IERC2981.sol";

interface IArtwork is IERC2981, IERC721 {
    struct ArtworkData {
        bytes32 hash;
        uint256[] traitTokenIds;
    }

    struct StringStorageData {
      uint8 stringStorageSlot;
      address stringStorageAddress;
    }

    error OnlyProjectRegistry();
    error ProofAlreadyMinted();
    error TraitsAlreadySet();
    error TraitsNotSet();
    error OnlyArtist();
    error OnlyArtistOrProjectRegistry();
    error OnlyArtworkOwner();
    error AlreadySetup();

    event ArtworkMinted(
        uint256 indexed artworkTokenId,
        uint256[] traitTokenIds,
        bytes32 hash,
        address indexed creator
    );
    event TraitsReclaimed(
        uint256 indexed artworkTokenId,
        address indexed caller
    );

    /**
     * Sets up the contract
     *
     * @param _data bytes containing address of the traits contract
     */
    function setup(bytes calldata _data) external;

    /**
     * Mints a new artwork with the specified trait token IDs
     * The caller must own the specified traits
     *
     * @param _traitTokenIds the trait token IDs to create the artwork with
     * @param _saltNonce salt number that is used to generate the artwork hash
     * @return _artworkTokenId the token ID of the newly minted artwork
     */
    function mintArtwork(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) external returns (uint256 _artworkTokenId);

    /**
     * Reclaims the traits the specified artwork token into the traits its composed of
     * The artwork token is burned, and the token ID will never be reused
     * The traits are transferred to the caller's address
     *
     * @param _artworkTokenId the token ID of the artwork being reclaimed
     */
    function reclaimTraits(uint256 _artworkTokenId) external;

    /**
     * Allows the artist or project registry to mint the proof mint
     *
     * @param _traitTokenIds token IDs of the traits to use to create the artwork
     * @param _saltNonce salt number that is used to generate the artwork hash
     */
    function mintArtworkProof(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) external;

    /**
     * Allows an address on the whitelist to mint an artwork for free
     *
     * @param _traitTokenIds token IDs of the traits to use to create the artwork
     * @param _saltNonce salt number that is used to generate the artwork hash
     */
    function mintArtworkWhitelist(
        uint256[] calldata _traitTokenIds,
        uint256 _saltNonce
    ) external;

    /**
     * Mints traits with specified amounts, and mints an artwork in a single transaction
     * ETH amount needed for trait mints must be sent
     *
     * @param _traitTokenIdsToBuy token IDs of the traits to buy
     * @param _traitAmountsToBuy amounts of each trait to buy
     * @param _traitTokenIdsToCreateArtwork token IDs of the traits to use to create the artwork
     * @param _saltNonce salt number that is used to generate the artwork hash
     */
    function mintTraitsAndArtwork(
        uint256[] calldata _traitTokenIdsToBuy,
        uint256[] calldata _traitAmountsToBuy,
        uint256[] calldata _traitTokenIdsToCreateArtwork,
        uint256 _saltNonce
    ) external payable;

    /**
     * Returns the URI of the specified token ID
     *
     * @param _tokenId token ID to get URI for
     * @return string the token URI
     */
    function tokenURI(uint256 _tokenId) external view returns (string memory);

    /**
     * Returns info about the specified artwork token
     *
     * @param _artworkTokenId token ID of the artwork
     * @return _traitTokenIds the token IDs of the traits the artwork contains
     * @return _traitNames the human readable trait names contained in the artwork
     * @return _traitValues the trait values used by the generative script
     * @return _traitTypeNames the human readable trait type names
     * @return _traitTypeValues the trait type values used by the generative script
     * @return _hash the artwork hash
     */
    function artwork(
        uint256 _artworkTokenId
    )
        external
        view
        returns (
            uint256[] memory _traitTokenIds,
            string[] memory _traitNames,
            string[] memory _traitValues,
            string[] memory _traitTypeNames,
            string[] memory _traitTypeValues,
            bytes32 _hash
        );

    /**
     * Returns a string containing the project metadata in JSON format
     *
     * @return string the project metadata in JSON format
     */
    function metadataJSON() external view returns (string memory);

    /**
     * Returns the string that contains the generative art javascript
     *
     * @return string the generative art javascript
     */
    function script() external view returns (string memory);

    /**
     * Returns the specified user's nonce, which is used to generate artwork hashes
     *
     * @param _user the address of the user
     * @return uint256 the user's nonce
     */
    function userNonce(address _user) external view returns (uint256);

    /**
     * Returns whether the specified interface ID is supported by the contract
     *
     * @param interfaceId the interface ID to check
     * @return bool True if the interface is supported, otherwise False
     */
    function supportsInterface(bytes4 interfaceId) external view returns (bool);
}

contracts/interfaces/IStringStorage.sol.content:
// SPDX-License-Identifier: GNU GPLv3
pragma solidity =0.8.19;

interface IStringStorage {
  error EmptySlot();

  /**
   * Returns the string stored at the specific slot
   * Reverts if an invalid slot is provided
   *
   * @param _slot the slot to get the string at
   * @return string the string stored at the specified slot
   */
  function stringAtSlot(uint8 _slot) external pure returns (string memory);
}

contracts/interfaces/IProjectRegistry.so.content:
// SPDX-License-Identifier: GNU GPLv3
pragma solidity =0.8.19;

interface IProjectRegistry {
    struct Project {
        address artwork;
        address traits;
    }

    error OnlyAdmin();
    error InvalidAddress();
    error InvalidArrayLengths();

    event BaseURIUpdated(string baseURI);
    event ProjectRegistered(
        uint256 indexed projectId,
        address indexed artwork,
        address indexed traits
    );
    event AdminAdded(address indexed account);
    event AdminRemoved(address indexed account);

    /**
     * Updates the URI base string
     *
     * @param _baseURI the updated base URI string
     */
    function updateBaseURI(string memory _baseURI) external;

    /**
     * Registers a project by storing the Artwork and Traits contract addresses,
     * and calls setup on both contracts with the corresponding bytes data
     *
     * @param _artwork the address of the Artwork contract
     * @param _artworkData data to pass to setup function of the Artwork contract
     * @param _traits the address of the Traits contract
     * @param _traitsData data to pass to setup function of the Traits contract
     */
    function registerProject(
        address _artwork,
        bytes calldata _artworkData,
        address _traits,
        bytes calldata _traitsData
    ) external;

    /**
     * Executes an arbitrary number of external function calls
     *
     * @param _targets the array of addresses to call
     * @param _values array of Ether amounts for each transaction
     * @param _calldatas array of transaction calldata bytes
     */
    function execute(
        address[] calldata _targets,
        uint256[] calldata _values,
        bytes[] calldata _calldatas
    ) external;

    /**
     * Adds multiple addresses to be made admins
     *
     * @param _admins the addresses to make admins
     */
    function addAdmins(address[] memory _admins) external;

    /**
     * Removes multiple addresses from being admins
     *
     * @param _admins the addresses remove from being admins
     */
    function removeAdmins(address[] memory _admins) external;

    /**
     * Returns the base URI string
     *
     * @return the base URI string
     */
    function baseURI() external view returns (string memory);
}

